events.code:
// Review
if(K('form').reviewMode() || (K('form').submission().id())){
   searchConfig.personSearchBridgeTable.response = JSON.parse(K('submission').value('source-json'));
  searchConfig.personSearchBridgeTable.resultsContainerId = "sourceTable";
  DataViewer.renderResults(function(){ return $(K('content[source]').element());}, searchConfig.personSearchBridgeTable);

  searchConfig.personSearchBridgeTable.response = JSON.parse(K('submission').value('destination-json'));
  searchConfig.personSearchBridgeTable.resultsContainerId = "destinationTable";
  DataViewer.renderResults(function(){ return $(K('content[destination]').element());}, searchConfig.personSearchBridgeTable);


}
// Not Review
else{  
   searchConfig.personSearchBridgeTable.response = [];
  DataViewer.renderResults(function(){ return $(K('content[destination]').element());}, searchConfig.personSearchBridgeTable);
  searchConfig.personSearchBridgeTable.resultsContainerId = "sourceTable";
  DataViewer.executeSearch(function(){ return $(K('content[source]').element());}, searchConfig.personSearchBridgeTable);
}
